mapper.select:
SELECT * FROM cargo_table WHERE valid = 1

    AND cargo_id = #{cargoId}


    AND user_id = #{userId}


    AND cargo_type_id = #{cargoTypeId}


    AND gross = #{gross}


    AND container_id = #{containerId}

SELECT cargo_table.cargo_id AS 1_cargo_id, cargo_table.user_id AS 1_user_id, cargo_table.cargo_type_id AS 1_cargo_type_id, cargo_table.gross AS 1_gross, cargo_table.container_id AS 1_container_id, cargo_table.valid AS 1_valid, cargo_attr_table.cargo_type_id AS 2_cargo_type_id, cargo_attr_table.cargo_name AS 2_cargo_name, cargo_attr_table.container_type AS 2_container_type, cargo_attr_table.unit_type AS 2_unit_type, cargo_attr_table.valid AS 2_valid FROM cargo_table, cargo_attr_table WHERE cargo_table.cargo_type_id = cargo_attr_table.cargo_type_id AND cargo_table.valid = 1 AND cargo_attr_table.valid = 1
AND container_id = #{containerId}
AND container_type = #{containerType}

SELECT * FROM cargo_table WHERE valid = 0

    AND cargo_id = #{cargoId}


    AND user_id = #{userId}


    AND cargo_type_id = #{cargoTypeId}


    AND gross = #{gross}


    AND container_id = #{containerId}

SELECT *
FROM cargo_table
WHERE valid = 1 AND cargo_id = #{id}

mapper.update:
UPDATE cargo_table


        , user_id = #{userId}


        , cargo_type_id = #{cargoTypeId}


        , gross = #{gross}


        , container_id = #{containerId}


        , valid = #{valid}


WHERE cargo_id = #{cargoId}

UPDATE cargo_table
SET valid = 0
WHERE container_id = #{containerId}
